86739110cc7be4b02b9154330ae817fbe49bd638.source:
query Animes($page: Int, $perPage: Int) {
  Page(page: $page, perPage: $perPage) {
    __typename
    media(type: ANIME) {
      __typename
      id
      title {
        __typename
        romaji
      }
      coverImage {
        __typename
        large
      }
    }
  }
}

c70aa2eb985f619b2b31205d7933f8c23f9f7223.source:
query SeasonalAnime($page: Int, $perPage: Int, $season: MediaSeason, $seasonYear: Int, $sort: [MediaSort]) {
  Page(page: $page, perPage: $perPage) {
    __typename
    media(season: $season, seasonYear: $seasonYear, sort: $sort) {
      __typename
      id
      title {
        __typename
        romaji
      }
      coverImage {
        __typename
        large
      }
    }
  }
}

5f582fe74d9c97dd0b26578014b739bea5a8c6ab.source:
query TodayAiringAnimes($page: Int, $perPage: Int, $sort: [AiringSort], $airingAtGreater: Int, $airingAtLesser: Int) {
  Page(page: $page, perPage: $perPage) {
    __typename
    airingSchedules(
      sort: $sort
      airingAt_greater: $airingAtGreater
      airingAt_lesser: $airingAtLesser
    ) {
      __typename
      media {
        __typename
        id
        title {
          __typename
          romaji
        }
        coverImage {
          __typename
          large
        }
      }
      timeUntilAiring
    }
  }
}

5b8e6d0ad7da6e3a682dbfdea8f066bd31364b88.source:
query UserMediaList($page: Int, $perPage: Int, $userId: Int, $type: MediaType, $status: MediaListStatus, $sort: [MediaListSort]) {
  Page(page: $page, perPage: $perPage) {
    __typename
    mediaList(userId: $userId, type: $type, status: $status, sort: $sort) {
      __typename
      status
      score
      progress
      progressVolumes
      media {
        __typename
        id
        title {
          __typename
          userPreferred
        }
        episodes
        chapters
        volumes
        coverImage {
          __typename
          large
        }
        nextAiringEpisode {
          __typename
          episode
          airingAt
        }
        status
        type
      }
    }
    pageInfo {
      __typename
      hasNextPage
    }
  }
}

