af08a183a12daf451c727ad622b7a2111f850a7f.source:
query Animes($page: Int, $perPage: Int, $sort: [MediaSort]) {
  Page(page: $page, perPage: $perPage) {
    __typename
    media(type: ANIME, sort: $sort) {
      __typename
      id
      title {
        __typename
        romaji
      }
      coverImage {
        __typename
        large
      }
    }
  }
}

74e9be4ba7204d8c7373f611b329f0e4f4250b08.source:
query MediaChart($page: Int, $perPage: Int, $sort: [MediaSort], $type: MediaType) {
  Page(page: $page, perPage: $perPage) {
    __typename
    media(sort: $sort, type: $type) {
      __typename
      id
      title {
        __typename
        romaji
      }
      format
      startDate {
        __typename
        year
      }
      coverImage {
        __typename
        large
      }
    }
    pageInfo {
      __typename
      hasNextPage
      currentPage
    }
  }
}

1c24bcef25cbe199bb4d3ce02372c55e7fdaa279.source:
query MediaDetails($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    title {
      __typename
      romaji
      english
      native
    }
    format
    status
    description
    startDate {
      __typename
      ...FuzzyDate
    }
    endDate {
      __typename
      ...FuzzyDate
    }
    season
    seasonYear
    episodes
    duration
    chapters
    volumes
    coverImage {
      __typename
      large
      color
    }
    bannerImage
    averageScore
    meanScore
    popularity
    genres
    studios {
      __typename
      nodes {
        __typename
        name
        isAnimationStudio
      }
    }
    favourites
    type
    nextAiringEpisode {
      __typename
      timeUntilAiring
      episode
    }
    mediaListEntry {
      __typename
      status
      score
      progress
      progressVolumes
      startedAt {
        __typename
        ...FuzzyDate
      }
      completedAt {
        __typename
        ...FuzzyDate
      }
    }
    source
  }
}
fragment FuzzyDate on FuzzyDate {
  __typename
  day
  month
  year
}

