5f582fe74d9c97dd0b26578014b739bea5a8c6ab.source:
query TodayAiringAnimes($page: Int, $perPage: Int, $sort: [AiringSort], $airingAtGreater: Int, $airingAtLesser: Int) {
  Page(page: $page, perPage: $perPage) {
    __typename
    airingSchedules(
      sort: $sort
      airingAt_greater: $airingAtGreater
      airingAt_lesser: $airingAtLesser
    ) {
      __typename
      media {
        __typename
        id
        title {
          __typename
          romaji
        }
        coverImage {
          __typename
          large
        }
      }
      timeUntilAiring
    }
  }
}

86e29646168ed3e503a6822e05a3f107d762bbb0.source:
mutation UpdateEntry($mediaId: Int, $status: MediaListStatus, $score: Float, $progress: Int, $progressVolumes: Int, $startedAt: FuzzyDateInput, $completedAt: FuzzyDateInput) {
  SaveMediaListEntry(
    mediaId: $mediaId
    status: $status
    score: $score
    progress: $progress
    progressVolumes: $progressVolumes
    startedAt: $startedAt
    completedAt: $completedAt
  ) {
    __typename
    id
    mediaId
  }
}

c5e2751c73bc60a908aa3ddbcc4b67fc2979d906.source:
mutation UpdateEntryProgress($mediaId: Int, $progress: Int) {
  SaveMediaListEntry(mediaId: $mediaId, progress: $progress) {
    __typename
    id
    mediaId
    progress
  }
}

e4485dc7a5dafc7486b71a340d691e8c3f87b669.source:
query UserMediaList($page: Int, $perPage: Int, $userId: Int, $type: MediaType, $status: MediaListStatus, $sort: [MediaListSort]) {
  Page(page: $page, perPage: $perPage) {
    __typename
    mediaList(userId: $userId, type: $type, status: $status, sort: $sort) {
      __typename
      id
      mediaId
      status
      score
      progress
      progressVolumes
      media {
        __typename
        title {
          __typename
          userPreferred
        }
        episodes
        chapters
        volumes
        coverImage {
          __typename
          large
        }
        nextAiringEpisode {
          __typename
          episode
          airingAt
        }
        status
        type
      }
    }
    pageInfo {
      __typename
      hasNextPage
    }
  }
}

