60dc2dc4e4c06506a236421001960a6429b27b2e.source:
query MediaCharactersAndStaff($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    characters(page: 1, perPage: 25) {
      __typename
      edges {
        __typename
        ...MediaCharacter
      }
    }
    staff(page: 1, perPage: 25) {
      __typename
      edges {
        __typename
        ...MediaStaff
      }
    }
  }
}
fragment MediaCharacter on CharacterEdge {
  __typename
  role
  node {
    __typename
    id
    name {
      __typename
      userPreferred
    }
    image {
      __typename
      medium
    }
  }
  voiceActors(language: JAPANESE) {
    __typename
    id
    name {
      __typename
      userPreferred
    }
    image {
      __typename
      medium
    }
  }
}
fragment MediaStaff on StaffEdge {
  __typename
  role
  node {
    __typename
    id
    name {
      __typename
      userPreferred
    }
    image {
      __typename
      medium
    }
  }
}

