ce17f7f6664ed29558afabb9ed511c7333b7f951.source:
query MediaDetails($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    title {
      __typename
      romaji
      english
      native
    }
    format
    status
    description
    startDate {
      __typename
      ...FuzzyDate
    }
    endDate {
      __typename
      ...FuzzyDate
    }
    season
    seasonYear
    episodes
    duration
    chapters
    volumes
    coverImage {
      __typename
      large
      extraLarge
      color
    }
    bannerImage
    averageScore
    meanScore
    popularity
    genres
    studios {
      __typename
      nodes {
        __typename
        name
        isAnimationStudio
      }
    }
    favourites
    type
    nextAiringEpisode {
      __typename
      timeUntilAiring
      episode
    }
    mediaListEntry {
      __typename
      id
      status
      score
      progress
      progressVolumes
      startedAt {
        __typename
        ...FuzzyDate
      }
      completedAt {
        __typename
        ...FuzzyDate
      }
    }
    source
    externalLinks {
      __typename
      id
      url
      site
      type
      icon
    }
  }
}
fragment FuzzyDate on FuzzyDate {
  __typename
  day
  month
  year
}

b7b2eb5c629dd5a5c2b003863110d541d35a4ab3.source:
query MediaRelationsAndRecommendations($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    relations {
      __typename
      edges {
        __typename
        ...MediaRelated
      }
    }
    recommendations(page: 1, sort: RATING_DESC) {
      __typename
      nodes {
        __typename
        ...MediaRecommended
      }
    }
  }
}
fragment MediaRelated on MediaEdge {
  __typename
  relationType
  node {
    __typename
    id
    title {
      __typename
      userPreferred
    }
    format
    coverImage {
      __typename
      large
    }
  }
}
fragment MediaRecommended on Recommendation {
  __typename
  mediaRecommendation {
    __typename
    id
    title {
      __typename
      userPreferred
    }
    format
    coverImage {
      __typename
      large
    }
  }
}

