aa70f90140c6e0c7fc9e3e4b959f1482d5599475.source:
query MediaCharactersAndStaff($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    characters(page: 1, perPage: 25) {
      __typename
      edges {
        __typename
        ...MediaCharacter
      }
    }
    staff(page: 1, perPage: 25) {
      __typename
      edges {
        __typename
        ...MediaStaff
      }
    }
  }
}
fragment MediaCharacter on CharacterEdge {
  __typename
  role
  node {
    __typename
    id
    name {
      __typename
      full
    }
    image {
      __typename
      medium
    }
  }
  voiceActors(language: JAPANESE) {
    __typename
    id
    name {
      __typename
      full
    }
    image {
      __typename
      medium
    }
  }
}
fragment MediaStaff on StaffEdge {
  __typename
  role
  node {
    __typename
    id
    name {
      __typename
      full
    }
    image {
      __typename
      medium
    }
  }
}

