1c24bcef25cbe199bb4d3ce02372c55e7fdaa279.source:
query MediaDetails($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    title {
      __typename
      romaji
      english
      native
    }
    format
    status
    description
    startDate {
      __typename
      ...FuzzyDate
    }
    endDate {
      __typename
      ...FuzzyDate
    }
    season
    seasonYear
    episodes
    duration
    chapters
    volumes
    coverImage {
      __typename
      large
      color
    }
    bannerImage
    averageScore
    meanScore
    popularity
    genres
    studios {
      __typename
      nodes {
        __typename
        name
        isAnimationStudio
      }
    }
    favourites
    type
    nextAiringEpisode {
      __typename
      timeUntilAiring
      episode
    }
    mediaListEntry {
      __typename
      status
      score
      progress
      progressVolumes
      startedAt {
        __typename
        ...FuzzyDate
      }
      completedAt {
        __typename
        ...FuzzyDate
      }
    }
    source
  }
}
fragment FuzzyDate on FuzzyDate {
  __typename
  day
  month
  year
}

17ea6fb12df12741c68325b6610f351ae2032d69.source:
query MediaRelationsAndRecommendations($mediaId: Int) {
  Media(id: $mediaId) {
    __typename
    relations {
      __typename
      edges {
        __typename
        ...MediaRelated
      }
    }
    recommendations(page: 1, sort: RATING_DESC) {
      __typename
      nodes {
        __typename
        ...MediaRecommended
      }
    }
  }
}
fragment MediaRelated on MediaEdge {
  __typename
  relationType
  node {
    __typename
    id
    title {
      __typename
      romaji
    }
    format
    coverImage {
      __typename
      large
    }
  }
}
fragment MediaRecommended on Recommendation {
  __typename
  mediaRecommendation {
    __typename
    id
    title {
      __typename
      romaji
    }
    format
    coverImage {
      __typename
      large
    }
  }
}

